//
// Config section
//

define("NET_BDD_HOST", ""); //MySQL host
define("NET_BDD_USERNAME", ""); //MySQL username
define("NET_BDD_PASSWORD", ""); //MySQL password
define("NET_BDD_DATABASE", ""); //Database name
define("NET_BDD_CHARSET", "UTF-8"); //Database charset

//
// DO NOT TOUCH THE CODE BELLOW THIS LINE
//
define("NET_ERROR_LAYOUT", "");

function net_error($code = "Error", $message) {
	$clean_layout = str_replace(array("%code%", "%message%"), array($code, $message), base64_decode(NET_ERROR_LAYOUT));
	die($clean_layout);
}

class veloce_mini_bdd_handler {

	private $link;
	
	public function __construct() {

		try 
		{
			$this->link = new PDO('mysql:host='.NET_BDD_HOST.';dbname='.NET_BDD_DATABASE.';charset='.NET_BDD_CHARSET, NET_BDD_USERNAME, NET_BDD_PASSWORD);
		} 
		catch(Exception $e)
		{
			net_error($e->getCode(), $e->getMessage());
		}
		
	}

	public function setActiveTable($table) {

		$this->ActiveTable = $table;

	}

	public function get($string, $values=array(), $fetchAll=0) {

		if ($string === "-all") {

			$prepare = $this->link->prepare("SELECT * FROM `".$this->ActiveTable."`");
			$prepare->execute();

		} else {

			$prepare = $this->link->prepare("SELECT * FROM `".$this->ActiveTable."` WHERE ".$string);
			$prepare->execute($values);

		}

		if ($fetchAll != 1) return $prepare; else return $prepare->fetchAll();

	}

	public function put($column, $string, $values=array()) {

		$prepare = $this->link->prepare("INSERT INTO `".$this->ActiveTable."`(".$column.") VALUES(".$string.")");
		$prepare->execute($values);
		return $prepare;
	}

	public function query($string, $values=array(), $fetchAll=0) {

		$sql = str_replace(array("%table%", "'"), array($this->ActiveTable, "`"), $string);
		$prepare = $this->link->prepare($sql);
		$prepare->execute($values);
		if ($fetchAll != 1) return $prepare; else return $prepare->fetch();

	}

	public function delete($where, $values) {

		$prepare = $this->link->prepare("DELETE FROM `".$this->ActiveTable."` WHERE ".$where);
		$prepare->execute($values);

	}

}
$v_bdd = new veloce_mini_bdd_handler();

class veloce_mini_html {
